export function stripeRequestByRouter(route, options) {
  options.baseUrl = stripeServer;
  const request = options.request = options.request || {};
  request.authorizationBearer = options.privateKey;
  return requestByRouter(route, options);
}

function simpleGroupsToSmart(groups) {
  const groupMapper = group => {
    if (group.groups) {
      group.groups = group.groups.map(groupMapper);
    }

    if (group.apis) {
      group.apis = group.apis.map(api => simpleRouteToSmart(api, groups));
    }

    return group;
  };

  return groups.map(groupMapper);
}

function isObject(obj) {
  return obj && typeof obj === 'object' && !(obj instanceof Array);
}

function getAllGroupsSave(groups) {
  return groups.map(group => {
    const newGroup = { ...group
    }; // remove apis smarts

    if (newGroup.apis) {
      newGroup.apis = newGroup.apis.map(api => {
        const newApi = { ...api
        };
        delete newApi.smarts;
        delete newApi.result;
        Object.keys(newApi).forEach(key => {
          if (key.indexOf('result.') === 0 || key.indexOf('request.') === 0) {
            delete newApi[key];
          }
        });
        return newApi;
      });
    } // recurse


    if (newGroup.groups) {
      newGroup.groups = getAllGroupsSave(newGroup.groups);
    }

    return newGroup;
  });
}

export function download(filename, text) {
  var element = document.createElement('a');
  element.setAttribute('href', 'data:text/html;charset=utf-8,' + encodeURIComponent(text));
  element.setAttribute('download', filename);
  element.style.display = 'none';
  document.body.appendChild(element);
  element.click();
  document.body.removeChild(element);
}
